import React from "react";
import PropTypes from "prop-types";
import {Card,Alert} from 'react-bootstrap';

function Profile({
    fullName, 
    profession, 
    bio, 
    handleName,img}) {
    return (
    <div>
        <Alert variant="secondary">{handleName(fullName)}</Alert>
        <Card className="mb-3 mt-3" style={{ color: "#000" }}>
            <Card.Img src={img.children}>{img.children}</Card.Img>
            <Card.Body>
                <Card.Title><h2>{fullName}</h2></Card.Title>
                <Card.Text><h3>{profession}</h3></Card.Text>
                <Card.Text><h4>{bio}</h4></Card.Text>
            </Card.Body>
        </Card>
    </div>
    );
}

Profile.propTypes = {
    fullName: PropTypes.string.isRequired,
    profession: PropTypes.string.isRequired,
    bio: PropTypes.string.isRequired,
};

Profile.defaultProps = {
    fullName: "ABDOUL AZIZ ALLASSANI",
    profession: "Developper",
    bio: "Hi humans! Abdoul Aziz here, I like development, new challenges, profitable business and sports. I like to have fun whenever I get the chance.",
};

export default Profile;
